package com.testweb.leecode;/** * Created by xingxuechao on 16/5/31. */public class Easy {    public static String reverse(String s){        char[] chars = s.toCharArray();        StringBuilder sb = new StringBuilder();        for (int i = chars.length-1; i >= 0; i--) {            sb.append(chars[i]);        }        return sb.toString();    }    public static void main(String args[]){        System.out.print("sdfsdf");        System.out.print(reverse("1234567890"));    }}